import { 
    Entity, 
    Column, 
    PrimaryGeneratedColumn, 
    OneToOne, 
    JoinColumn
  } from 'typeorm'
  import {Product} from '../entities/product.entity'

@Entity({ name: 'Details' })
  export class Detail {
    @PrimaryGeneratedColumn()
    id: number
  
    @Column()
    discount: number
  
    @Column()
    unit_price: number

    @Column()
    quantity: number

    @OneToOne(() => Product, (product) => product.detail)
    @JoinColumn({ name: 'product_id' })
    product: Product
  }
