import { 
  Entity, 
  Column, 
  PrimaryGeneratedColumn, 
  OneToOne 
} from 'typeorm'
import {Detail} from '../entities/detail.entity'

@Entity({ name: 'products' })
export class Product {
  @PrimaryGeneratedColumn()
  id: number

  @Column({ unique: true })
  name: string
  @Column()
  size: number

  @OneToOne(() => Detail, (detail) => detail.product)
  detail: Detail

 @Column()
  description: string
}
